import React,{useEffect,useState} from 'react';
import firebase from '../../firebase'

const ItemEditForm = ({item}) => {

  const [dataItem,setDataItem] = useState(null);


  useEffect(() =>{
      firebase.firestore().collection('inventory').doc(item).get().then(function(doc) {
      if (doc.exists) {
           setDataItem(doc.data());
      } else {
          // doc.data() will be undefined in this case

      }
  })

  }, [])

  const submitEdit = (event) => {
    console.log(event.target)





    event.preventDefault();
  }

    return(
      <>
      { dataItem ?


        <form onSubmit={submitEdit} > {/*why do the brackets need to be ommited here something about event becoming undefined.*/}

        <label for="name">Item Name:</label>
        <input type="text" name="name" id="name" defaultValue={  dataItem.itemName} />

        <label for="quantity">Quantity:</label>
        <input type="number" name="quantity" id="quantity" min="0" defaultValue={  dataItem.quantity} />

        <label for="dop">Date of Purchase:</label>
        <input type="text" name="dop" id="dop" defaultValue={  dataItem.purchaseDate} />

        <label for="ed">Expiry Date:</label>
        <input type="text" name="ed" id="ed" defaultValue={  dataItem.expiryDate} />

        <label for="pp">Purchased Price</label>
        <input type="number" name="pp" id="pp" step="0.01" min="0" defaultValue={  dataItem.purchaseP} />

        <label for="supplier">Supplier:</label>
        <input type="submit" value="save" />
        </form>
         : <h1>loading</h1>
      }
      </>
    )



}

export default ItemEditForm;
