import React, {Component} from 'react';
import { Button } from 'react-bootstrap';
import fire from '../../fire';

class HomepageContent extends Component {






 handleSubmit = (e) => {
  const nameInput = document.getElementById('inputText');
  const ageInput = document.getElementById('ageText');
  const addBtn =  document.getElementById('ageText');

 const database = firebase.database();
   e.preventDefault();
   database.ref('/users/').set({
     name: nameInput.value,
     age: ageInput.value
   });

 };



  render(){
    return(
    <>
    <div className="mb-2">
      <Button variant="primary" size="lg">
        Inventory
      </Button>{' '}{' '}
      <Button variant="secondary" size="lg">
      Suppliers
      </Button>{' '}
      <Button variant="primary" size="lg">
        Add New Suppliers
      </Button>{' '}
      <Button variant="secondary" size="lg">
        Add New Inventory
      </Button>{' '}
      <Button variant="primary" size="lg">
        Edit Inventory
      </Button>{' '}
      <Button variant="secondary" size="lg">
        Edit Suppliers
      </Button>
        <div>
          <form>
          <input  type="text" id="inputText" placeholder="name"/>
          <input type="text" id="ageText" placeholder="age"/>
          <button onClick={this.handleSubmit}>Submit</button>
          </form>
        </div>
    </div>
    </> 
    );
  }
}


export default HomepageContent;
